SELECT ISNULL( RIGHT( '00000' + CONVERT( VARCHAR, ( CONVERT( INT, MAX( CODE ) ) + 1 ) ), 10 ), RIGHT( '00000' + CONVERT( VARCHAR, ( CONVERT( INT, #{parentCode} ) + 1 ) ), 10 ) ) AS attrCode FROM COMMON_CODE_INFO WHERE PARENT_CODE = #{parentCode}
SELECT RIGHT( '00000' + CONVERT( VARCHAR, ( CONVERT( INT, LEFT( MAX( CODE ), 5 ) ) + 1 ) ) + '00000', 10 ) FROM COMMON_CODE_INFO WHERE PARENT_CODE = ''
INSERT INTO COMMON_CODE_INFO ( CODE, PARENT_CODE, CODE_NAME, CODE_DESC, CODE_ORDER, USE_YN, INSERT_USER, INSERT_DATE ) VALUES ( #{code}, #{parentCode}, #{codeName}, #{codeDesc}, #{codeOrder}, #{useYN}, #{userName}, GETDATE() )
